import {GET_POSTS, ADD_POST, UPDATE_POST, TOGGLE_IS_READY} from "../consts";

export const getPosts = items => ({
    type: GET_POSTS,
    payload: items
});

export const addPosts = text => ({
    type: ADD_POST,
    text
});

