import React from "react";
import style from "../styles/post.module.css"

export const Post = ({post}) =>{
  return <div className={style.post}>
    <div>
      <img src={post.avatar} alt=""/>
    </div>
    <div>
      <p>{post.text}</p>
      <p>{post.createdAt.slice(0,10)}</p>
    </div>

  </div>
};
