import React from "react";
import Form from "./Form";
import {Post} from "./Post";


const Home = ({posts, isReady}) => {

    const addPost = (data) => {
console.log(data);
    };

    return (
        <div>
            <div>
                <Form onSubmit={addPost}/>
            </div>
            {!isReady
                ? <div>Loading</div>
                : <div>
                    {posts.map( post =>
                        <Post post={post}
                         key={post.id}/>
                    )}
                </div>

            }


        </div>

    )

};

export default Home;
