import React, {useEffect} from "react";
import Home from "../components/Home";
import {connect} from "react-redux";
import {getAllPosts} from "../thunks/posts";

const HomeContainer = ({posts, getAllPosts, isReady}) => {

    useEffect(() => {
        getAllPosts();
    }, []);

    return <Home
        posts={posts}
        isReady={isReady}/>

};

const mapStateToProps = (state) => ({
    posts: state.posts.posts,
    isReady: state.posts.isReady
});

export default connect(mapStateToProps, {getAllPosts})(HomeContainer);
