import React from "react";
import {Field, reduxForm} from "redux-form";
import {maxLength100, required} from "../helpers/validators";
import {Textarea} from "./FormControls";

let Form = ({handleSubmit}) => {

    const submitMyForm = (data) => {
        const {createRecord, resetForm} = this.props;
        return createRecord(data).then(() => {
            resetForm();
        });
    }

    return (
        <form onSubmit={handleSubmit} >
            <div className="form-group">
                <Field
                    component={Textarea}
                    element={"textarea"}
                    validate={[required,maxLength100]}
                    name="message"
                    placeholder="Start messaging here"
                    />
            </div>
            <div className="text-right">
                <button type="submit" className="btn btn-primary "> Send</button>
            </div>
        </form>
    )
};

export default Form = reduxForm({form: 'form'})(Form);
